import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {fetchOrders} from '../features/orders/ordersSlice';
import type { RootState, AppDispatch } from '../app/store';
import { removeItem, updateQuantity } from '../features/carts/cartsSlice';

const Orders: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const orders = useSelector((state: RootState) => state.orders.orders);
  const loading = useSelector((state: RootState) => state.orders.loading);
  const error = useSelector((state: RootState) => state.orders.error);
  const cart = useSelector((state: RootState) => state.carts);

  useEffect(() => {
    dispatch(fetchOrders());
  }, [dispatch]);

  const handleRemoveItem = (productId: number) => {
    dispatch(removeItem(productId));
  };

  const handleUpdateQuantity = (productId: number, quantity: number) => {
    if (quantity > 0) {
      dispatch(updateQuantity({ product_id: productId, quantity }));
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Your Orders</h1>
      {orders && orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <ul>
          {orders && orders.map(order => (
            <li key={order.order_id}>
              <h2>Order ID: {order.order_id}</h2>
              <p>Status: {order.status}</p>
              <p>Total: {order.total_xaf} XAF</p>
              <p>Created At: {new Date(order.created_at).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
      <h1>Your Shopping Cart</h1>
      {!cart || !cart.items || cart.items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          <ul>
            {cart.items.map(item => (
              <li key={item.product_id}>
                <span>Product ID: {item.product_id}</span>
                <span>Quantity: {item.quantity}</span>
                <span>Price: {item.total_price_xaf} XAF</span>
                <button onClick={() => handleRemoveItem(item.product_id)}>Remove</button>
                <input
                  type="number"
                  min={1}
                  value={item.quantity}
                  onChange={(e) => handleUpdateQuantity(item.product_id, Number(e.target.value))}
                />
              </li>
            ))}
          </ul>
          <div>
            <h2>Subtotal: {cart.subtotal_xaf} XAF</h2>
            <h2>Total: {cart.total_xaf} XAF</h2>
            <button>Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Orders;
