import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import type { RootState } from '../app/store';
import { removeItem, updateQuantity } from '../features/carts/cartsSlice';

const Cart: React.FC = () => {
  const dispatch = useDispatch();
  const cart = useSelector((state: RootState) => state.carts);

  const handleRemoveItem = (productId: number) => {
    dispatch(removeItem(productId));
  };

  const handleUpdateQuantity = (productId: number, quantity: number) => {
    dispatch(updateQuantity({ product_id: productId, quantity }));
  };

  return (
    <div>
      <h1>Your Shopping Cart</h1>
      {cart.items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          <ul>
            {cart.items.map(item => (
              <li key={item.product_id}>
                <span>{item.name}</span>
                <span>Quantity: {item.quantity}</span>
                <span>Price: {item.total_price_xaf} XAF</span>
                <button onClick={() => handleRemoveItem(item.product_id)}>Remove</button>
                <input
                  type="number"
                  value={item.quantity}
                  onChange={(e) => handleUpdateQuantity(item.product_id, Number(e.target.value))}
                />
              </li>
            ))}
          </ul>
          <div>
            <h2>Subtotal: {cart.subtotal_xaf} XAF</h2>
            <h2>Total: {cart.total_xaf} XAF</h2>
            <button>Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
