import usersReducer from '../features/users/usersSlice';
import productsReducer from '../features/products/productsSlice';
import cartsReducer from '../features/carts/cartsSlice';
import ordersReducer from '../features/orders/ordersSlice';
import paymentsReducer from '../features/payments/paymentsSlice';
import shipmentsReducer from '../features/shipments/shipmentsSlice';
import { configureStore } from '@reduxjs/toolkit';

// Sample initial data
const initialProducts = [
  {
    id: 101,
    name: '150W Portable Power Station',
    category: 'Power Stations',
    brand: 'Generic',
    description: 'Compact 150Wh solar portable station with LED light.',
    price_xaf: 109000,
    stock: 25,
    image_url: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: 102,
    name: 'DP Super Rugged Solar Kit',
    category: 'Power Stations',
    brand: 'DP',
    description: '40Wh solar kit with 3 bulbs and mobile charging.',
    price_xaf: 51000,
    stock: 50,
    image_url: 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80',
  },
  {
    id: 103,
    name: 'Jackery Solar Generator 1000',
    category: 'Power Stations',
    brand: 'Jackery',
    description: '1000Wh solar generator for homes and offices.',
    price_xaf: 705000,
    stock: 10,
    image_url: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80',
  },
];

const initialUsers = [
  {
    id: 1,
    name: 'John Doe',
    email: 'john@example.com',
    password: 'hashed_password_123',
    phone: '+237650000001',
    role: 'customer',
    created_at: '2025-01-01T10:00:00Z',
  },
  {
    id: 2,
    name: 'Admin User',
    email: 'admin@example.com',
    password: 'hashed_password_admin',
    phone: '+237650000002',
    role: 'admin',
    created_at: '2025-01-01T09:00:00Z',
  },
];

const initialOrders = [
  {
    order_id: 'ORD789',
    cart_id: 'CART123',
    user_id: 1,
    status: 'paid',
    payment_id: 'PAY456',
    shipping_id: 'SHIP001',
    total_xaf: 205400,
    created_at: '2025-08-28T09:00:00Z',
  },
];

const preloadedState = {
  users: {
    users: initialUsers,
    currentUser: null,
    loading: false,
    error: null,
  },
  products: { products: initialProducts },
  orders: { orders: initialOrders },
};

const store = configureStore({
  reducer: {
    users: usersReducer,
    products: productsReducer,
    carts: cartsReducer,
    orders: ordersReducer,
    payments: paymentsReducer,
    shipments: shipmentsReducer,
  },
  preloadedState,
});

export type RootState = ReturnType<typeof store.getState>;
export type AppDispatch = typeof store.dispatch;
export default store;
