import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import clearCart from '../features/carts/cartsSlice';
import { createOrder } from '../features/orders/ordersSlice';
import { processPayment } from '../features/payments/paymentsSlice';

const Checkout: React.FC = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const cart = useSelector((state: any) => state.carts);

  const handleCheckout = async () => {
    try {
      // Process payment
      await dispatch(processPayment(cart.total_xaf));

      // Create order
      await dispatch(createOrder(cart));

      // Clear cart after successful order
      dispatch(clearCart({}));
      navigate('/orders'); // Redirect to orders page
    } catch (error) {
      // Handle error (optional: show error message)
      console.error('Checkout failed:', error);
    }
  };

  return (
    <div>
      <h1>Checkout</h1>
      <div>
        <h2>Order Summary</h2>
        <p>Subtotal: {cart.subtotal_xaf} XAF</p>
        <p>Discount: {cart.discount_xaf} XAF</p>
        <p>Shipping: {cart.shipping_xaf} XAF</p>
        <p>Tax: {cart.tax_xaf} XAF</p>
        <h3>Total: {cart.total_xaf} XAF</h3>
      </div>
      <button onClick={handleCheckout}>Complete Purchase</button>
    </div>
  );
};

export default Checkout;
